(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),s=document.querySelectorAll("#timer span"),m=document.createElement("span"),r=document.createElement("span");let d;r.textContent=" : ",m.textContent="00",m.id="timer-days",s[0].before(m),s[0].before(r);const i=()=>{let t=(new Date(e).getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,days:Math.floor(t/60/60/24),hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}};d=i();d.timeRemaining>0&&setInterval((()=>{let e=i();e.days<10?m.textContent="0"+e.days:m.textContent=e.days,e.hours<10?t.textContent="0"+e.hours:t.textContent=e.hours,e.minutes<10?n.textContent="0"+e.minutes:n.textContent=e.minutes,e.seconds<10?o.textContent="0"+e.seconds:o.textContent=e.seconds}),1e3)})("31 december 2021")})();